mapper.update:
update t_account_log
set status = #{status}
where trade_no = #{tradeNo}

mapper.select:
select trade_no tradeNo, pk_value pkValue, type, amount, gmt_time gmtTime, info, tx_id, action_id, status
from t_account_log
where pk_value = #{pkValue}
  and type = #{type}

select trade_no tradeNo, pk_value pkValue, type, amount, gmt_time gmtTime, info, tx_id, action_id, status
from t_account_log
where pk_value = #{pk}
  and status = #{status}

select trade_no tradeNo, pk_value pkValue, type, amount, gmt_time gmtTime, info, tx_id txId, action_id actionId, status
from t_account_log
where tx_id = #{txId}
